import { Select,Table, Tag, Space ,Col} from 'antd';
import React from "react";
import {getFetch} from "../function/fetch";
import {BrowserRouter} from "react-router-dom";

const { Column, ColumnGroup } = Table;

export default class TicketInfoPage extends React.Component {
    constructor() {
        super();
        this.state = {
            data:null,
            types:null
        }
        // getFetch("/ticket/all","",(rsp)=>this.setState({
        //     data:rsp
        // }))
    }

    componentDidMount() {
        getFetch("/ticket/all","",(rsp)=>{
            console.log(rsp)
            this.setState({
                data:rsp
            })
        })
        getFetch("/ticket/allTypes","",(rsp)=>{
            console.log(rsp);
            let children=[];
            for(let i=0;i<rsp.length;i++){
                children.push(<Select key={rsp[i].type_name}>{i.toString(36) + i}</Select>);
            }
        })
    }

    // componentWillUnmount() {
    //     getFetch("/ticket/all","",(rsp)=>this.setState({
    //         data:rsp
    //     }))
    // }

    render() {
        return(
            <Col span={12} offset={6}>
                <Select style={{ width: 120 }}>
                    {this.state.types}
                </Select>
            <Table dataSource={this.state.data}>
                <Column title="ticket name" dataIndex="ticketName"/>
                <Column title="price" dataIndex="price"/>
                <Column title="available nums" dataIndex="availableNumbers"/>
                <Column
                    title=""
                    key="action"
                    render={(record) => (
                            <a onClick={()=>this.props.history.push("/ticketDetail/"+record.tid)}>详情</a>
                    )}
                />
            </Table>
            </Col>
        );
    }
}
