import React from "react";
import {Button, Checkbox, Form, Input} from 'antd';
import 'antd/dist/antd.css';

export default class LoginPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: "",
            PW: "",
        }
    }


    render() {
        return (
            <div>
                <div style={{
                    maxWidth: "500px",
                    padding: "15px",
                    margin: "0 auto"
                }}>
                    <Form
                        className="login-form"
                        initialValues={{
                            remember: true,
                        }}
                    >
                        <Form.Item
                            name="email"
                            rules={[
                                {
                                    required: true,
                                    message: '请输入邮箱',
                                },
                            ]}
                        >
                            <Input
                                id="emailInput"
                                placeholder="邮箱"
                                onChange={(e) => {
                                    this.setState({email: e.target.value})
                                }}
                            />
                        </Form.Item>
                        <Form.Item
                            name="password"
                            rules={[
                                {
                                    required: true,
                                    message: '请输入密码',
                                },
                            ]}
                        >
                            <Input
                                id="pwInput"
                                type="password"
                                placeholder="密码"
                                onChange={(e) => {
                                    this.setState({PW: e.target.value})
                                }}
                            />
                        </Form.Item>
                        <Form.Item>
                            <Form.Item name="remember" valuePropName="checked" noStyle>
                                <Checkbox>记住我</Checkbox>
                            </Form.Item>

                            <a className="login-form-forgot">
                                <span onClick={()=>this.props.history.push("/findPW")}>
                                    忘记密码
                                </span>
                            </a>
                        </Form.Item>

                        <Form.Item>
                            <Button
                                data-cy="login"
                                type="primary"
                                htmlType="submit"
                                className="login-form-button"
                                onClick={() => this.Login()}
                            >
                                登录
                            </Button>
                            或者 <a data-cy="goToRegister"><span
                            onClick={() => this.props.history.push("/register")}>立刻注册
                            </span>
                        </a>
                        </Form.Item>
                    </Form>
                </div>
            </div>
        )
    }
}


let loginFetch = () => {
    let id = document.getElementById("id").value;
    let pw = document.getElementById("password").value;
    fetch('http://192.168.1.106:8080/login?id='+id+"&password="+pw,{
        method:'POST',
        mode:'cors',
        cache:'default'
    }).then(rsp => rsp.json().then(rsp => {
        if(rsp.status==401)
            alert(rsp.msg);
        else {

        }
    }))
};
